import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer } from "recharts";

export default function SensorChart({ data }) {
    // data expected sorted asc by createdAt
    const mapped = data.map(d => ({ ...d, createdAt: new Date(d.createdAt).toLocaleTimeString() }));
    return (
        <div className="bg-white p-4 rounded-xl shadow-md">
            <h3 className="mb-4 font-semibold">Biểu đồ</h3>
            <ResponsiveContainer width="100%" height={300}>
                <LineChart data={mapped}>
                    <CartesianGrid strokeDasharray="3 3" />
                    <XAxis dataKey="createdAt" />
                    <YAxis />
                    <Tooltip />
                    <Line type="monotone" dataKey="temperature" stroke="#ff4d4f" dot />
                    <Line type="monotone" dataKey="humidity" stroke="#1890ff" dot />
                    <Line type="monotone" dataKey="light" stroke="#52c41a" dot />
                </LineChart>
            </ResponsiveContainer>
        </div>
    );
}
