import React, { useState } from "react";

export default function Table({
                                  columns,
                                  data,
                                  rowsPerPageOptions = [5, 10, 20],
                                  enableDeviceFilter = false,
                                  enableDateFilter = false,
                              }) {
    const [page, setPage] = useState(0);
    const [rowsPerPage, setRowsPerPage] = useState(rowsPerPageOptions[0]);

    // Bộ lọc
    const [deviceFilter, setDeviceFilter] = useState("");
    const [startDate, setStartDate] = useState("");
    const [endDate, setEndDate] = useState("");

    // Lọc dữ liệu theo điều kiện
    let filteredData = data;

    if (enableDeviceFilter && deviceFilter) {
        filteredData = filteredData.filter(
            (row) =>
                row.device &&
                row.device.toLowerCase() === deviceFilter.toLowerCase()
        );
    }

    if (enableDateFilter && (startDate || endDate)) {
        filteredData = filteredData.filter((row) => {
            const time = new Date(row.timestamp);
            const afterStart = startDate ? time >= new Date(startDate) : true;
            const beforeEnd = endDate ? time <= new Date(endDate) : true;
            return afterStart && beforeEnd;
        });
    }

    // Pagination
    const start = page * rowsPerPage;
    const end = start + rowsPerPage;
    const paginatedData = filteredData.slice(start, end);
    const totalPages = Math.ceil(filteredData.length / rowsPerPage);

    const handlePrev = () => page > 0 && setPage(page - 1);
    const handleNext = () => page < totalPages - 1 && setPage(page + 1);
    const handleRowsChange = (e) => {
        setRowsPerPage(Number(e.target.value));
        setPage(0);
    };

    return (
        <div>
            {/* Bộ lọc */}
            <div className="flex flex-wrap gap-4 mb-4 items-center text-sm">
                {enableDeviceFilter && (
                    <div>
                        <label className="mr-2">Device:</label>
                        <select
                            value={deviceFilter}
                            onChange={(e) => {
                                setDeviceFilter(e.target.value);
                                setPage(0);
                            }}
                            className="border rounded p-1"
                        >
                            <option value="">All</option>
                            {[...new Set(data.map((d) => d.device))].map((dev, i) => (
                                <option key={i} value={dev}>
                                    {dev}
                                </option>
                            ))}
                        </select>
                    </div>
                )}

                {enableDateFilter && (
                    <div className="flex gap-2 items-center">
                        <label>From:</label>
                        <input
                            type="date"
                            value={startDate}
                            onChange={(e) => {
                                setStartDate(e.target.value);
                                setPage(0);
                            }}
                            className="border rounded p-1"
                        />
                        <label>To:</label>
                        <input
                            type="date"
                            value={endDate}
                            onChange={(e) => {
                                setEndDate(e.target.value);
                                setPage(0);
                            }}
                            className="border rounded p-1"
                        />
                    </div>
                )}
            </div>

            {/* Bảng */}
            <div className="overflow-auto rounded-xl data-table">
                <table className="min-w-full">
                    <thead>
                    <tr>
                        {columns.map((col, idx) => (
                            <th key={idx} className="p-3 text-left">
                                {col.header}
                            </th>
                        ))}
                    </tr>
                    </thead>
                    <tbody>
                    {paginatedData.length === 0 ? (
                        <tr>
                            <td colSpan={columns.length} className="p-4 text-center text-gray-400">
                                No data available
                            </td>
                        </tr>
                    ) : (
                        paginatedData.map((row, rowIndex) => (
                            <tr
                                key={rowIndex}
                                className="odd:bg-white even:bg-gray-50/50 hover:bg-gray-100"
                            >
                                {columns.map((col, colIndex) => (
                                    <td key={colIndex} className="p-3">
                                        {typeof col.accessor === "function"
                                            ? col.accessor(row)
                                            : row[col.accessor]}
                                    </td>
                                ))}
                            </tr>
                        ))
                    )}
                    </tbody>
                </table>
            </div>

            {/* Pagination controls */}
            <div className="flex items-center justify-between mt-4 text-sm">
                <div>
                    Rows per page:{" "}
                    <select
                        value={rowsPerPage}
                        onChange={handleRowsChange}
                        className="border rounded p-1"
                    >
                        {rowsPerPageOptions.map((opt) => (
                            <option key={opt} value={opt}>
                                {opt}
                            </option>
                        ))}
                    </select>
                </div>

                <div className="flex items-center gap-2">
                    <button
                        onClick={handlePrev}
                        disabled={page === 0}
                        className="px-3 py-1 rounded bg-gray-200 disabled:opacity-50"
                    >
                        Prev
                    </button>
                    <span>
            Page {page + 1} of {totalPages || 1}
          </span>
                    <button
                        onClick={handleNext}
                        disabled={page >= totalPages - 1}
                        className="px-3 py-1 rounded bg-gray-200 disabled:opacity-50"
                    >
                        Next
                    </button>
                </div>
            </div>
        </div>
    );
}
