// src/App.jsx

import React from 'react';
// Import BrowserRouter từ react-router-dom
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom'; 

import Sidebar from './components/Sidebar'; 
import Dashboard from './pages/Dashboard';
import DataSensor from './pages/DataSensor';
import ActionsHistory from './pages/ActionsHistory';
import Profile from './pages/Profile';
import './index.css'; 
// Đảm bảo bạn đã cài đặt react-router-dom: npm install react-router-dom

const App = () => {
    return (
        // Đặt Router bao bọc toàn bộ cấu trúc ứng dụng
        <Router>
            {/* main-layout định nghĩa Sidebar và Content nằm cạnh nhau */}
            <div className="main-layout"> 
                
                {/* 1. Sidebar (sử dụng NavLink) ĐÃ nằm trong Router */}
                <Sidebar />
                
                {/* 2. Khu vực nội dung chính */}
                <div className="content-area"> 
                    {/* Routes phải nằm trong Content Area để chuyển đổi nội dung */}
                    <Routes>
                        <Route path="/" element={<Dashboard />} />
                        <Route path="/data-sensor" element={<DataSensor />} />
                        <Route path="/actions-history" element={<ActionsHistory />} />
                        <Route path="/profile" element={<Profile />} />
                    </Routes>
                </div>
            </div>
        </Router>
    );
};

export default App;
