// src/components/Table.jsx
import React, { useState, useMemo } from 'react';
// import './Table.css'; // Đảm bảo bạn có file CSS này nếu cần

/**
 * Component Table chung có Phân trang và hỗ trợ Sắp xếp
 * @param {Array} headers - Mảng tiêu đề cột (sử dụng làm keys)
 * @param {Array} data - Dữ liệu bảng (mảng các đối tượng)
 * @param {string} title - Tiêu đề của bảng
 * @param {function} onSortChange - Hàm xử lý sắp xếp từ component cha
 * @param {string} sortBy - Cột đang được sắp xếp
 * @param {string} sortOrder - Thứ tự sắp xếp ('asc' hoặc 'desc')
 */
const Table = ({ 
    headers = [], 
    data = [], // Đảm bảo giá trị mặc định là mảng rỗng để ngăn lỗi .slice()
    title = 'Dữ liệu Bảng', 
    initialPageSize = 10,
    onSortChange, 
    sortBy, 
    sortOrder 
}) => {
    
    const [currentPage, setCurrentPage] = useState(1);
    const [pageSize, setPageSize] = useState(initialPageSize);
    
    // Đảm bảo data luôn là một mảng trước khi thao tác
    const memoizedData = useMemo(() => {
        return Array.isArray(data) ? data : [];
    }, [data]);

    // Logic tính toán phân trang
    const totalItems = memoizedData.length;
    const totalPages = Math.ceil(totalItems / pageSize);
    const startIndex = (currentPage - 1) * pageSize;
    
    // currentData chứa dữ liệu của trang hiện tại
    const currentData = memoizedData.slice(startIndex, startIndex + pageSize); 

    const handlePageChange = (newPage) => {
        if (newPage >= 1 && newPage <= totalPages) {
            setCurrentPage(newPage);
        }
    };
    
    const handlePageSizeChange = (e) => {
        const newSize = parseInt(e.target.value);
        setPageSize(newSize);
        setCurrentPage(1); // Reset về trang 1 khi thay đổi kích thước trang
    };

    return (
        <div className="custom-table-container card">
            <div className="table-header-controls">
                <h3 className="table-title">{title}</h3>
                <div className="pagination-controls">
                    
                    <label className="page-size-label">
                        Kích thước:
                        <select value={pageSize} onChange={handlePageSizeChange} className="page-size-select">
                            <option value={5}>5</option>
                            <option value={10}>10</option>
                            <option value={20}>20</option>
                        </select>
                    </label>

                    <span className="page-info">
                        Trang {currentPage} trên {totalPages}
                    </span>
                    
                    <button onClick={() => handlePageChange(currentPage - 1)} disabled={currentPage === 1} className="page-button">
                        &lt;
                    </button>
                    <button onClick={() => handlePageChange(currentPage + 1)} disabled={currentPage === totalPages} className="page-button">
                        &gt;
                    </button>
                </div>
            </div>

            <div className="table-wrapper">
                <table className="data-table">
                    <thead>
                        <tr>
                            {headers.map((header, index) => (
                                <th 
                                    key={index} 
                                    onClick={() => onSortChange && onSortChange(header)} // Kích hoạt sắp xếp từ component cha
                                    className={`sortable ${sortBy === header ? 'active' : ''}`}
                                >
                                    {header}
                                    
                                    {/* Biểu tượng Sắp xếp */}
                                    {sortBy === header && (
                                        <span className="sort-icon">
                                            {sortOrder === 'asc' ? ' ▲' : ' ▼'}
                                        </span>
                                    )}
                                </th>
                            ))}
                        </tr>
                    </thead>
                    <tbody>
                        {currentData.length > 0 ? (
                            currentData.map((row, rowIndex) => (
                                <tr key={rowIndex}>
                                    {/* Duyệt qua các cột trong hàng */}
                                    {headers.map((header, colIndex) => (
                                        <td key={colIndex}>
                                            {/* Hiển thị giá trị tương ứng với header */}
                                            {row[header]}
                                        </td>
                                    ))}
                                </tr>
                            ))
                        ) : (
                            <tr>
                                <td colSpan={headers.length} className="no-data-message">
                                    {totalItems === 0 ? "Không có dữ liệu để hiển thị." : "Không tìm thấy dữ liệu trên trang này."}
                                </td>
                            </tr>
                        )}
                    </tbody>
                </table>
            </div>
        </div>
    );
};

export default Table;
