// frontend/src/pages/ActionsHistory.jsx (PHẦN ĐÃ SỬA)

import React, { useEffect, useState } from 'react';
import { getActionsHistory } from '../api'; 
import './ActionsHistory.css'; 

const ActionsHistory = () => {
    const [actions, setActions] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState(null);
    
    // Thêm states cho điều khiển bảng
    const [pageSize, setPageSize] = useState(10);
    const [currentPage, setCurrentPage] = useState(1);
    const [searchTerm, setSearchTerm] = useState('');
    const [filterDevice, setFilterDevice] = useState('Tất cả'); // Thêm state cho bộ lọc thiết bị

    useEffect(() => {
        const fetchActions = async () => {
            try {
                const data = await getActionsHistory(); 
                // MySQL trả về ID là chữ hoa. Nếu không phải, bạn phải sửa lại đây.
                setActions(data); 
            } catch (err) {
                console.error("Lỗi khi tải lịch sử hành động:", err);
                setError("Không thể tải dữ liệu lịch sử. Kiểm tra Server Backend và CSDL.");
            } finally {
                setIsLoading(false);
            }
        };

        fetchActions();
    }, []);

    // --- LOGIC LỌC/PHÂN TRANG ---
    const filteredActions = actions.filter(action => {
        // Lấy ID: Chúng ta ưu tiên 'ID' (hoa) nhưng sẽ thử cả 'id' (thường)
        const actionId = action.ID || action.id || ''; 
        
        // Tạo chuỗi tìm kiếm từ ID, Device, Status
        const searchString = `${actionId} ${action.Device} ${action.Status}`.toLowerCase();
        
        // 1. Lọc theo Tìm kiếm chung (Search Term)
        const matchesSearch = searchString.includes(searchTerm.toLowerCase());
        
        // 2. Lọc theo Thiết bị (Device Filter)
        const matchesDevice = filterDevice === 'Tất cả' || action.Device.toLowerCase() === filterDevice.toLowerCase();

        return matchesSearch && matchesDevice;
    });
    
    // Phân trang
    const totalPages = Math.ceil(filteredActions.length / pageSize);
    const startIndex = (currentPage - 1) * pageSize;
    const endIndex = startIndex + pageSize;
    const currentActions = filteredActions.slice(startIndex, endIndex);

    // Hàm định dạng thời gian (Giữ nguyên)
    const formatTime = (timeString) => {
        if (!timeString) return '--';
        const date = new Date(timeString);
        return date.toLocaleString('vi-VN', {
            year: 'numeric',
            month: '2-digit',
            day: '2-digit',
            hour: '2-digit',
            minute: '2-digit',
            second: '2-digit'
        });
    };

    if (isLoading) {
        return <div className="actions-history-page">Đang tải lịch sử...</div>;
    }

    if (error) {
        return <div className="actions-history-page error">{error}</div>;
    }

    return (
        <div className="actions-history-page">
            <h2>Lịch sử Hành động Điều khiển</h2>

            {/* KHU VỰC ĐIỀU KHIỂN BẢNG */}
            <div className="table-controls">
                <div className="search-group">
                    {/* Bộ lọc Device/Type */}
                    <select 
                        className="filter-select" 
                        value={filterDevice}
                        onChange={(e) => {
                            setFilterDevice(e.target.value);
                            setCurrentPage(1); // Reset về trang 1
                        }}
                    >
                        <option value="Tất cả">Tất cả</option>
                        <option value="light">light</option>
                        <option value="fan">fan</option>
                        <option value="ac">ac</option>
                    </select>
                    
                    <input 
                        type="text" 
                        placeholder="Tìm kiếm trong Tất cả..." 
                        value={searchTerm}
                        onChange={(e) => {
                            setSearchTerm(e.target.value);
                            setCurrentPage(1); // Reset về trang 1
                        }}
                    />
                    <button className="search-button">Search</button>
                </div>
            </div>

            <div className="table-container">
                <table className="actions-table">
                    <thead>
                        <tr>
                            <th>ID</th> 
                            <th>Thiết bị (device)</th>
                            <th>Trạng thái (status)</th>
                            <th>Thời gian (created_at)</th>
                        </tr>
                    </thead>
                    <tbody>
                        {currentActions.length === 0 ? (
                            <tr>
                                <td colSpan="4">Không tìm thấy hành động nào.</td>
                            </tr>
                        ) : (
                            currentActions.map((action) => (
                                <tr key={action.ID || action.id}> 
                                    {/* SỬA LỖI ID: Ưu tiên action.ID, nếu không có thì thử action.id */}
                                    <td>{action.ID || action.id}</td> 
                                    <td>{action.Device}</td>
                                    <td className={`status-${action.Status.toLowerCase()}`}>{action.Status}</td>
                                    <td>{formatTime(action.Time)}</td>
                                </tr>
                            ))
                        )}
                    </tbody>
                </table>
            </div>

            {/* KHU VỰC PHÂN TRANG */}
            <div className="pagination-controls">
                <span>Kích thước:</span>
                <select value={pageSize} onChange={(e) => {
                    setPageSize(parseInt(e.target.value));
                    setCurrentPage(1);
                }}>
                    <option value={5}>5</option>
                    <option value={10}>10</option>
                    <option value={20}>20</option>
                </select>
                
                <span>Trang {currentPage} trên {totalPages}</span>

                <button 
                    disabled={currentPage === 1} 
                    onClick={() => setCurrentPage(currentPage - 1)}
                >
                    &lt;
                </button>
                <button 
                    disabled={currentPage === totalPages} 
                    onClick={() => setCurrentPage(currentPage + 1)}
                >
                    &gt;
                </button>
            </div>
        </div>
    );
};

export default ActionsHistory;
