export function ControlCard({ title, device }) {
    const [on, setOn] = React.useState(false);
    const toggle = async () => {
        const next = !on;
        setOn(next);
        // gọi API điều khiển
        await fetch(`/api/devices/${device}`, {
            method: "POST",
            headers: {'Content-Type':'application/json'},
            body: JSON.stringify({ status: next ? "ON" : "OFF" })
        });
    };
    return (
        <div className="control-card flex flex-col gap-4">
            <div className="text-sm opacity-80">{title}</div>
            <div className="flex items-center justify-between">
                <div className={`toggle ${on ? "on":""}`} onClick={toggle}>
                    <div className="dot"></div>
                </div>
                <div className="text-sm">{on ? "ON" : "OFF"}</div>
            </div>
        </div>
    );
}
