import React, { useEffect, useState } from "react";
import API from "../api";
import io from "socket.io-client";
import SensorCard from "../components/SensorCard";
import { ControlCard as DeviceCard } from '../components/DeviceCard';

import SensorChart from "../components/SensorChart";

const socket = io("http://localhost:5000");

export default function Dashboard(){
    const [sensors, setSensors] = useState([]);
    useEffect(()=>{
        API.get("/sensors?limit=30").then(res => {
            // sort ascending
            const sorted = res.data.slice().reverse();
            setSensors(sorted);
        });
        socket.on("sensor-data", (d) => {
            setSensors(prev => [...prev.slice(-29), d]); // keep last 30
        });
        return ()=> socket.off("sensor-data");
    },[]);

    const latest = sensors.length ? sensors[sensors.length-1] : null;

    return (
        <div>
            <h1 className="text-2xl font-bold mb-4">Dashboard</h1>
            <div className="grid grid-cols-3 gap-4 mb-6">
                <SensorCard title="Nhiệt độ" value={latest ? latest.temperature : "--"} unit="°C" />
                <SensorCard title="Ánh sáng" value={latest ? latest.light : "--"} unit="Lux" />
                <SensorCard title="Độ ẩm" value={latest ? latest.humidity : "--"} unit="%" />
            </div>

            <div className="flex gap-6">
                <div className="space-y-4">
                    <DeviceCard name="Nhiệt độ" device="ac" />
                    <DeviceCard name="Ánh sáng" device="light" />
                    <DeviceCard name="Độ ẩm" device="fan" />
                </div>

                <div className="flex-1">
                    <SensorChart data={sensors} />
                </div>
            </div>
        </div>
    );
}
