// src/pages/ActionsHistory.jsx

import React, { useEffect, useState, useMemo } from 'react';
import { getActionsHistory } from '../api'; 
import Table from '../components/Table'; 
import './ActionsHistory.css'; 

const ActionsHistory = () => {
    // ... (Các states giữ nguyên)
    const [actions, setActions] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState(null);
    const [searchTerm, setSearchTerm] = useState('');
    const [filterDevice, setFilterDevice] = useState('Tất cả'); 
    const [sortBy, setSortBy] = useState('Time'); 
    const [sortOrder, setSortOrder] = useState('desc'); 
    const actionHeaders = ['ID', 'Device', 'Status', 'Time'];

    // Hàm định dạng thời gian (Giữ nguyên)
    const formatTime = (timeString) => {
        if (!timeString) return '--';
        const date = new Date(timeString);
        return date.toLocaleString('vi-VN', {
            year: 'numeric', month: '2-digit', day: '2-digit',
            hour: '2-digit', minute: '2-digit', second: '2-digit'
        });
    };
    
    // ... (useEffect và handleSortChange giữ nguyên)

    // LOGIC LỌC/SẮP XẾP/CHUẨN BỊ DỮ LIỆU DÙNG useMemo (ĐÃ SỬA PHẦN LỌC)
    const displayData = useMemo(() => {
        let currentActions = [...actions];
        
        // 1. Lọc theo Tìm kiếm và Thiết bị (PHẦN ĐÃ SỬA)
        currentActions = currentActions.filter(action => {
            const lowerSearch = searchTerm.toLowerCase();
            
            // Lấy các giá trị đã định dạng để tìm kiếm
            const formattedTime = formatTime(action.Time).toLowerCase(); // ĐỊNH DẠNG THỜI GIAN
            const actionId = String(action.ID || action.id || '').toLowerCase(); 
            const device = String(action.Device || '').toLowerCase();
            const status = String(action.Status || '').toLowerCase();
            
            // Xây dựng chuỗi tìm kiếm tổng hợp (bao gồm cả thời gian đã định dạng)
            const searchString = `${actionId} ${device} ${status} ${formattedTime}`;

            // 1a. Lọc theo Tìm kiếm chung (Bây giờ bao gồm cả thời gian)
            const matchesSearch = searchString.includes(lowerSearch);
            
            // 1b. Lọc theo Thiết bị
            const matchesDevice = filterDevice === 'Tất cả' || device === filterDevice.toLowerCase();
            
            return matchesSearch && matchesDevice;
        });

        // 2. Sắp xếp (Giữ nguyên)
        if (sortBy) {
            currentActions.sort((a, b) => {
                const aValue = a[sortBy];
                const bValue = b[sortBy];
                
                if (sortBy === 'Time') {
                    const aTime = new Date(aValue).getTime();
                    const bTime = new Date(bValue).getTime();
                    if (aTime < bTime) return sortOrder === 'asc' ? -1 : 1;
                    if (aTime > bValue) return sortOrder === 'asc' ? 1 : -1;
                    return 0;
                }
                
                const valA = String(aValue ?? '').toLowerCase();
                const valB = String(bValue ?? '').toLowerCase();

                if (valA < valB) return sortOrder === 'asc' ? -1 : 1;
                if (valA > valB) return sortOrder === 'asc' ? 1 : -1;
                return 0;
            });
        }

        // 3. CHUẨN BỊ DỮ LIỆU ĐỂ HIỂN THỊ (Định dạng thời gian)
        const formattedData = currentActions.map(item => ({
            ...item,
            Time: formatTime(item.Time) 
        }));
        
        return formattedData;
    }, [actions, searchTerm, filterDevice, sortBy, sortOrder]); // Thêm action.Time vào dependencies

    // ... (if isLoading, if error giữ nguyên)

    return (
        <div className="actions-history-page">
            <h2>Lịch sử Hành động Điều khiển</h2>

            {/* KHU VỰC ĐIỀU KHIỂN BẢNG */}
            <div className="table-controls">
                <div className="search-group">
                    {/* Bộ lọc Type/Device */}
                    <select 
                        className="filter-select" 
                        value={filterDevice}
                        onChange={(e) => setFilterDevice(e.target.value)}
                    >
                        <option value="Tất cả">Tất cả</option>
                        <option value="light">light</option>
                        <option value="fan">fan</option>
                        <option value="ac">ac</option>
                    </select>
                    
                    {/* INPUT TÌM KIẾM (Đã bao gồm tìm kiếm thời gian) */}
                    <input 
                        type="text" 
                        placeholder="Tìm kiếm ID, thiết bị, trạng thái, hoặc Thời gian..." 
                        value={searchTerm}
                        onChange={(e) => setSearchTerm(e.target.value)}
                    />
                    <button className="search-button">Search</button>
                </div>
            </div>

            <div className="table-container">
                <Table 
                    headers={actionHeaders} 
                    data={displayData} 
                    title="Lịch sử Hành động Điều khiển"
                    onSortChange={handleSortChange} 
                    sortBy={sortBy}
                    sortOrder={sortOrder}
                />
            </div>
        </div>
    );
};

export default ActionsHistory;
