// src/pages/Profile.jsx (ƒê√É S·ª¨A)

import React, { useEffect, useState, useMemo, useRef } from 'react'; // Import useRef
import './Profile.css'; 

const Profile = () => {
    // Th√™m useRef ƒë·ªÉ tham chi·∫øu ƒë·∫øn input type="file"
    const fileInputRef = useRef(null); 

    // Gi·∫£ ƒë·ªãnh d·ªØ li·ªáu ng∆∞·ªùi d√πng
    const [userData, setUserData] = useState({
        fullName: 'L√™ ƒê·ª©c To√†n',
        studentId: 'B22DCCN730',
        github: 'https://github.com/ToanVY/IoT.git',
        figma: 'https://www.figma.com/design/R03h45Jnuu58OsxUIhrxlv/IoT?node-id=0-1',
        email: 'letoan5204@gmail.com',
        // Kh·ªüi t·∫°o b·∫±ng ·∫£nh m·∫∑c ƒë·ªãnh ho·∫∑c URL ·∫£nh c≈©
        avatarUrl: 'https://i.ibb.co/LdQ8v5Q/profile-avatar.png' // Th·ª≠ d√πng m·ªôt placeholder URL
    });

    const [isEditing, setIsEditing] = useState(false);

    const handleEditToggle = () => {
        setIsEditing(!isEditing);
        if (isEditing) {
            console.log('D·ªØ li·ªáu ƒë√£ l∆∞u:', userData);
            // TODO: G·ª≠i userData v√† file ·∫£nh (n·∫øu c√≥) l√™n server
        }
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        setUserData(prevData => ({
            ...prevData,
            [name]: value
        }));
    };
    
    // --- H√ÄM X·ª¨ L√ù CH·ªåN ·∫¢NH M·ªöI ---
    const handleAvatarChange = (e) => {
        const file = e.target.files[0];
        if (file && file.type.startsWith('image/')) {
            const reader = new FileReader();
            reader.onloadend = () => {
                // C·∫≠p nh·∫≠t ngay l·∫≠p t·ª©c ƒë·ªÉ hi·ªÉn th·ªã ·∫£nh m·ªõi (Preview)
                setUserData(prevData => ({
                    ...prevData,
                    avatarUrl: reader.result // reader.result l√† URL Base64
                }));
                
                // TODO: B·∫°n c·∫ßn l∆∞u tr·ªØ ƒë·ªëi t∆∞·ª£ng File (file) ƒë·ªÉ g·ª≠i l√™n server khi nh·∫•n 'L∆∞u'
                // V√≠ d·ª•: setAvatarFile(file);
                console.log('ƒê√£ ch·ªçn file ·∫£nh:', file.name);
            };
            reader.readAsDataURL(file);
        }
    };
    
    // H√†m k√≠ch ho·∫°t input file khi nh·∫•n n√∫t "Thay ƒë·ªïi"
    const triggerFileInput = () => {
        fileInputRef.current.click();
    };

    return (
        <div className="profile-page">
            
            <div className="profile-card">
                
                <div className="profile-header">
                    <div className="avatar-section">
                        <div className="avatar-placeholder">
                            <img src={userData.avatarUrl} alt="·∫¢nh ƒë·∫°i di·ªán" className="profile-avatar"/>
                            
                            {/* INPUT FILE ·∫®N */}
                            <input 
                                type="file" 
                                ref={fileInputRef} // G√°n ref
                                onChange={handleAvatarChange} // X·ª≠ l√Ω thay ƒë·ªïi file
                                accept="image/*" // Ch·ªâ ch·∫•p nh·∫≠n file ·∫£nh
                                style={{ display: 'none' }} // ·∫®n input
                            />
                        </div>
                        
                        {/* N√∫t nh·∫•n s·∫Ω k√≠ch ho·∫°t input file */}
                        <button className="btn-secondary" onClick={triggerFileInput}>Thay ƒë·ªïi</button>
                    </div>

                    <div className="info-intro">
                        <h3>{userData.fullName}</h3>
                        <p>{userData.studentId}</p>
                    </div>

                    <button 
                        className="btn-primary" 
                        onClick={handleEditToggle}
                    >
                        {isEditing ? 'L∆∞u' : 'S·ª≠a'}
                    </button>
                </div>

                {/* Ph·∫ßn Form chi ti·∫øt (Gi·ªØ nguy√™n) */}
                <div className="profile-details">
                    
                    {/* H·ªå V√Ä T√äN */}
                    <div className="detail-row">
                        <label>H·ªç v√† t√™n</label>
                        <input
                            type="text"
                            name="fullName"
                            value={userData.fullName}
                            onChange={handleChange}
                            readOnly={!isEditing}
                            className={isEditing ? 'editable' : ''}
                        />
                    </div>

                    {/* M√É SV */}
                    <div className="detail-row">
                        <label>M√£ SV</label>
                        <input
                            type="text"
                            name="studentId"
                            value={userData.studentId}
                            onChange={handleChange}
                            readOnly={!isEditing}
                            className={isEditing ? 'editable' : ''}
                        />
                    </div>

                    {/* GITHUB */}
                    <div className="detail-row">
                        <label>Github</label>
                        <input
                            type="text"
                            name="github"
                            value={userData.github}
                            onChange={handleChange}
                            readOnly={!isEditing}
                            className={isEditing ? 'editable' : ''}
                        />
                    </div>
                    
                    {/* FIGMA */}
                    <div className="detail-row">
                        <label>Figma</label>
                        <input
                            type="text"
                            name="figma"
                            value={userData.figma}
                            onChange={handleChange}
                            readOnly={!isEditing}
                            className={isEditing ? 'editable' : ''}
                        />
                    </div>
                    
                    {/* EMAIL */}
                    <div className="detail-row">
                        <label>Email</label>
                        <input
                            type="email"
                            name="email"
                            value={userData.email}
                            onChange={handleChange}
                            readOnly={!isEditing}
                            className={isEditing ? 'editable' : ''}
                        />
                        <span className="email-icon">üìß</span>
                    </div>

                </div>
            </div>
        </div>
    );
};

export default Profile;
