// src/pages/Profile.jsx (ĐÃ SỬA)

import React, { useEffect, useState, useMemo, useRef } from 'react'; // Import useRef
import './Profile.css'; 

const Profile = () => {
    // Thêm useRef để tham chiếu đến input type="file"
    const fileInputRef = useRef(null); 

    // Giả định dữ liệu người dùng
    const [userData, setUserData] = useState({
        fullName: 'Lê Đức Toàn',
        studentId: 'B22DCCN730',
        github: 'https://github.com/ToanVY/IoT.git',
        figma: 'https://www.figma.com/design/R03h45Jnuu58OsxUIhrxlv/IoT?node-id=0-1',
        email: 'letoan5204@gmail.com',
        // Khởi tạo bằng ảnh mặc định hoặc URL ảnh cũ
        avatarUrl: 'https://i.ibb.co/LdQ8v5Q/profile-avatar.png' // Thử dùng một placeholder URL
    });

    const [isEditing, setIsEditing] = useState(false);

    const handleEditToggle = () => {
        setIsEditing(!isEditing);
        if (isEditing) {
            console.log('Dữ liệu đã lưu:', userData);
            // TODO: Gửi userData và file ảnh (nếu có) lên server
        }
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        setUserData(prevData => ({
            ...prevData,
            [name]: value
        }));
    };
    
    // --- HÀM XỬ LÝ CHỌN ẢNH MỚI ---
    const handleAvatarChange = (e) => {
        const file = e.target.files[0];
        if (file && file.type.startsWith('image/')) {
            const reader = new FileReader();
            reader.onloadend = () => {
                // Cập nhật ngay lập tức để hiển thị ảnh mới (Preview)
                setUserData(prevData => ({
                    ...prevData,
                    avatarUrl: reader.result // reader.result là URL Base64
                }));
                
                // TODO: Bạn cần lưu trữ đối tượng File (file) để gửi lên server khi nhấn 'Lưu'
                // Ví dụ: setAvatarFile(file);
                console.log('Đã chọn file ảnh:', file.name);
            };
            reader.readAsDataURL(file);
        }
    };
    
    // Hàm kích hoạt input file khi nhấn nút "Thay đổi"
    const triggerFileInput = () => {
        fileInputRef.current.click();
    };

    return (
        <div className="profile-page">
            
            <div className="profile-card">
                
                <div className="profile-header">
                    <div className="avatar-section">
                        <div className="avatar-placeholder">
                            <img src={userData.avatarUrl} alt="Ảnh đại diện" className="profile-avatar"/>
                            
                            {/* INPUT FILE ẨN */}
                            <input 
                                type="file" 
                                ref={fileInputRef} // Gán ref
                                onChange={handleAvatarChange} // Xử lý thay đổi file
                                accept="image/*" // Chỉ chấp nhận file ảnh
                                style={{ display: 'none' }} // Ẩn input
                            />
                        </div>
                        
                        {/* Nút nhấn sẽ kích hoạt input file */}
                        <button className="btn-secondary" onClick={triggerFileInput}>Thay đổi</button>
                    </div>

                    <div className="info-intro">
                        <h3>{userData.fullName}</h3>
                        <p>{userData.studentId}</p>
                    </div>

                    <button 
                        className="btn-primary" 
                        onClick={handleEditToggle}
                    >
                        {isEditing ? 'Lưu' : 'Sửa'}
                    </button>
                </div>

                {/* Phần Form chi tiết (Giữ nguyên) */}
                <div className="profile-details">
                    
                    {/* HỌ VÀ TÊN */}
                    <div className="detail-row">
                        <label>Họ và tên</label>
                        <input
                            type="text"
                            name="fullName"
                            value={userData.fullName}
                            onChange={handleChange}
                            readOnly={!isEditing}
                            className={isEditing ? 'editable' : ''}
                        />
                    </div>

                    {/* MÃ SV */}
                    <div className="detail-row">
                        <label>Mã SV</label>
                        <input
                            type="text"
                            name="studentId"
                            value={userData.studentId}
                            onChange={handleChange}
                            readOnly={!isEditing}
                            className={isEditing ? 'editable' : ''}
                        />
                    </div>

                    {/* GITHUB */}
                    <div className="detail-row">
                        <label>Github</label>
                        <input
                            type="text"
                            name="github"
                            value={userData.github}
                            onChange={handleChange}
                            readOnly={!isEditing}
                            className={isEditing ? 'editable' : ''}
                        />
                    </div>
                    
                    {/* FIGMA */}
                    <div className="detail-row">
                        <label>Figma</label>
                        <input
                            type="text"
                            name="figma"
                            value={userData.figma}
                            onChange={handleChange}
                            readOnly={!isEditing}
                            className={isEditing ? 'editable' : ''}
                        />
                    </div>
                    
                    {/* EMAIL */}
                    <div className="detail-row">
                        <label>Email</label>
                        <input
                            type="email"
                            name="email"
                            value={userData.email}
                            onChange={handleChange}
                            readOnly={!isEditing}
                            className={isEditing ? 'editable' : ''}
                        />
                        <span className="email-icon">📧</span>
                    </div>

                </div>
            </div>
        </div>
    );
};

export default Profile;
