// frontend/src/pages/ActionsHistory.jsx (PHIÊN BẢN HOÀN CHỈNH)

import React, { useEffect, useState, useMemo } from 'react';
import { getActionsHistory } from '../api'; 
import './ActionsHistory.css'; 

const ActionsHistory = () => {
    const [actions, setActions] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState(null);
    
    // States cho điều khiển bảng
    const [pageSize, setPageSize] = useState(10);
    const [currentPage, setCurrentPage] = useState(1);
    const [searchTerm, setSearchTerm] = useState('');
    const [filterDevice, setFilterDevice] = useState('Tất cả'); 
    
    // States cho sắp xếp
    const [sortColumn, setSortColumn] = useState('Time'); 
    const [sortDirection, setSortDirection] = useState('DESC'); 

    useEffect(() => {
        const fetchActions = async () => {
            try {
                const data = await getActionsHistory(); 
                setActions(data); 
            } catch (err) {
                console.error("Lỗi khi tải lịch sử hành động:", err);
                setError("Không thể tải dữ liệu lịch sử. Kiểm tra Server Backend và CSDL.");
            } finally {
                setIsLoading(false);
            }
        };

        fetchActions();
    }, []);

    // HÀM XỬ LÝ SẮP XẾP
    const handleSort = (column) => {
        if (sortColumn === column) {
            setSortDirection(sortDirection === 'ASC' ? 'DESC' : 'ASC');
        } else {
            setSortColumn(column);
            setSortDirection('DESC'); 
        }
    };
    
    // LOGIC LỌC/SẮP XẾP/PHÂN TRANG DÙNG useMemo
    const sortedAndFilteredActions = useMemo(() => {
        let currentActions = [...actions];
        
        // 1. Lọc theo Tìm kiếm và Thiết bị
        currentActions = currentActions.filter(action => {
            const actionId = action.ID || action.id || ''; 
            const searchString = `${actionId} ${action.Device} ${action.Status}`.toLowerCase();
            
            // Lọc theo Tìm kiếm chung
            const matchesSearch = searchString.includes(searchTerm.toLowerCase());
            
            // Lọc theo Thiết bị
            const deviceValue = action.Device ? action.Device.toLowerCase() : '';
            const matchesDevice = filterDevice === 'Tất cả' || deviceValue === filterDevice.toLowerCase();

            return matchesSearch && matchesDevice;
        });

        // 2. Sắp xếp
        if (sortColumn) {
            currentActions.sort((a, b) => {
                const columnA = sortColumn;
                const columnB = sortColumn === 'Time' ? 'Time' : (sortColumn === 'ID' ? 'ID' : sortColumn);
                
                let aValue = a[columnA] || a[columnA.toLowerCase()];
                let bValue = b[columnB] || b[columnB.toLowerCase()];
                
                if (sortColumn === 'Time') { // Sắp xếp theo thời gian
                    aValue = new Date(aValue).getTime();
                    bValue = new Date(bValue).getTime();
                } else if (sortColumn === 'ID') { // Sắp xếp số
                    aValue = Number(aValue);
                    bValue = Number(bValue);
                } else if (typeof aValue === 'string') { // Sắp xếp chuỗi
                    aValue = aValue.toLowerCase();
                    bValue = bValue.toLowerCase();
                }

                if (aValue < bValue) return sortDirection === 'ASC' ? -1 : 1;
                if (aValue > bValue) return sortDirection === 'ASC' ? 1 : -1;
                return 0;
            });
        }
        
        return currentActions;
    }, [actions, searchTerm, filterDevice, sortColumn, sortDirection]);

    // Phân trang
    const totalPages = Math.ceil(sortedAndFilteredActions.length / pageSize);
    const startIndex = (currentPage - 1) * pageSize;
    const endIndex = startIndex + pageSize;
    const currentActions = sortedAndFilteredActions.slice(startIndex, endIndex);

    // Hàm định dạng thời gian (Giữ nguyên)
    const formatTime = (timeString) => {
        if (!timeString) return '--';
        const date = new Date(timeString);
        return date.toLocaleString('vi-VN', {
            year: 'numeric',
            month: '2-digit',
            day: '2-digit',
            hour: '2-digit',
            minute: '2-digit',
            second: '2-digit'
        });
    };
    
    // Hàm hiển thị biểu tượng sắp xếp
    const renderSortIcon = (column) => {
        if (sortColumn !== column) return null;
        return sortDirection === 'ASC' ? ' ▲' : ' ▼';
    };


    if (isLoading) return <div className="actions-history-page">Đang tải lịch sử...</div>;
    if (error) return <div className="actions-history-page error">{error}</div>;

    return (
        <div className="actions-history-page">
            <h2>Lịch sử Hành động Điều khiển</h2>

            {/* KHU VỰC ĐIỀU KHIỂN BẢNG (ĐÃ SỬA ĐỂ HIỂN THỊ TÌM KIẾM) */}
            <div className="table-controls">
                <div className="search-group">
                    {/* Bộ lọc Type/Device */}
                    <select 
                        className="filter-select" 
                        value={filterDevice}
                        onChange={(e) => {
                            setFilterDevice(e.target.value);
                            setCurrentPage(1); // Reset về trang 1
                        }}
                    >
                        <option value="Tất cả">Tất cả</option>
                        <option value="light">light</option>
                        <option value="fan">fan</option>
                        <option value="ac">ac</option>
                    </select>
                    
                    {/* INPUT TÌM KIẾM ĐÃ KHÔI PHỤC */}
                    <input 
                        type="text" 
                        placeholder="Tìm kiếm trong Tất cả..." 
                        value={searchTerm}
                        onChange={(e) => {
                            setSearchTerm(e.target.value);
                            setCurrentPage(1); // Reset về trang 1 khi tìm kiếm
                        }}
                    />
                    <button className="search-button">Search</button>
                </div>
                {/* ĐẢM BẢO PHÂN TRANG NẰM Ở CUỐI (Tùy thuộc CSS, nếu muốn nó ở hàng dưới thì đặt ngoài table-controls) */}
                
            </div>
            
            <div className="table-container">
                <table className="actions-table">
                    <thead>
                        <tr>
                            <th onClick={() => handleSort('ID')} className="sortable">
                                ID {renderSortIcon('ID')}
                            </th> 
                            <th onClick={() => handleSort('Device')} className="sortable">
                                Thiết bị (device) {renderSortIcon('Device')}
                            </th>
                            <th onClick={() => handleSort('Status')} className="sortable">
                                Trạng thái (status) {renderSortIcon('Status')}
                            </th>
                            <th onClick={() => handleSort('Time')} className="sortable">
                                Thời gian (created_at) {renderSortIcon('Time')}
                            </th>
                        </tr>
                    </thead>
                    <tbody>
                        {currentActions.length === 0 ? (
                            <tr>
                                <td colSpan="4">Không tìm thấy hành động nào.</td>
                            </tr>
                        ) : (
                            currentActions.map((action) => (
                                <tr key={action.ID || action.id}> 
                                    {/* Sử dụng action.ID hoặc action.id */}
                                    <td>{action.ID || action.id}</td> 
                                    <td>{action.Device}</td>
                                    <td className={`status-${action.Status.toLowerCase()}`}>{action.Status}</td>
                                    <td>{formatTime(action.Time)}</td>
                                </tr>
                            ))
                        )}
                    </tbody>
                </table>
            </div>

            {/* KHU VỰC PHÂN TRANG (Giữ nguyên) */}
            <div className="pagination-controls">
                <span>Kích thước:</span>
                <select value={pageSize} onChange={(e) => {
                    setPageSize(parseInt(e.target.value));
                    setCurrentPage(1);
                }}>
                    <option value={5}>5</option>
                    <option value={10}>10</option>
                    <option value={20}>20</option>
                </select>
                
                <span>Trang {currentPage} trên {totalPages}</span>

                <button 
                    disabled={currentPage === 1} 
                    onClick={() => setCurrentPage(currentPage - 1)}
                >
                    &lt;
                </button>
                <button 
                    disabled={currentPage === totalPages} 
                    onClick={() => setCurrentPage(currentPage + 1)}
                >
                    &gt;
                </button>
            </div>
        </div>
    );
};

export default ActionsHistory;
