// src/pages/Dashboard.jsx (Phi√™n b·∫£n ƒë√£ c·∫≠p nh·∫≠t v·ªõi Socket.IO v√† Imports ƒë·∫ßy ƒë·ªß)

import React, { useEffect, useState } from 'react';
import { io } from 'socket.io-client';
import { getSensorData } from '../api';

// === IMPORT C√ÅC COMPONENTS C√íN THI·∫æU ===
import DeviceCard from '../components/DeviceCard'; 
import SensorChart from '../components/SensorChart';
import SensorCard from '../components/SensorCard';

// Kh·ªüi t·∫°o k·∫øt n·ªëi Socket.IO Client
const socket = io('http://localhost:5000'); 

// Danh s√°ch thi·∫øt b·ªã ƒëi·ªÅu khi·ªÉn (ƒê√£ ƒë∆∞·ª£c code tr∆∞·ªõc ƒë√≥)
const CONTROLLABLE_DEVICES = [
    { device: 'light', label: '√Ånh s√°ng', icon: 'üí°' },
    { device: 'temp', label: 'Nhi·ªát ƒë·ªô', icon: 'üå°Ô∏è' },
    { device: 'humid', label: 'ƒê·ªô ·∫©m', icon: 'üíß' },
];


const Dashboard = () => {
    // S·ª≠ d·ª•ng state ƒë·ªÉ l∆∞u tr·ªØ d·ªØ li·ªáu th·ªùi gian th·ª±c v√† d·ªØ li·ªáu bi·ªÉu ƒë·ªì
    const [latestData, setLatestData] = useState({});
    const [chartData, setChartData] = useState([]);
    const [loading, setLoading] = useState(true);

    // D√πng useEffect ƒë·ªÉ qu·∫£n l√Ω k·∫øt n·ªëi Socket.IO v√† t·∫£i d·ªØ li·ªáu l·ªãch s·ª≠
    useEffect(() => {
        
        // 1. T·∫£i d·ªØ li·ªáu l·ªãch s·ª≠ (API REST) cho bi·ªÉu ƒë·ªì m·ªôt l·∫ßn khi component mount
        const fetchInitialData = async () => {
            try {
                const data = await getSensorData(30); 
                setChartData(data.reverse()); 
                if (data.length > 0) {
                    setLatestData(data[data.length - 1]); 
                }
            } catch (error) {
                console.error("L·ªói t·∫£i d·ªØ li·ªáu Dashboard ban ƒë·∫ßu:", error);
                // N·∫øu l·ªói 500/k·∫øt n·ªëi, v·∫´n cho ph√©p Dashboard load v·ªõi d·ªØ li·ªáu r·ªóng
            } finally {
                setLoading(false);
            }
        };

        // 2. Thi·∫øt l·∫≠p l·∫Øng nghe Socket.IO (Real-time)
        socket.on('connect', () => {
            console.log('Socket.IO connected to server.');
        });
        
        // L·∫Øng nghe s·ª± ki·ªán 'sensorDataUpdate' t·ª´ server.js
        socket.on('sensorDataUpdate', (data) => {
            console.log('Real-time data received:', data);
            
            // C·∫≠p nh·∫≠t d·ªØ li·ªáu m·ªõi nh·∫•t
            setLatestData(data);
            
            // C·∫≠p nh·∫≠t bi·ªÉu ƒë·ªì: X√≥a b·∫£n ghi c≈© nh·∫•t v√† th√™m b·∫£n ghi m·ªõi
            setChartData(prevChartData => {
                // ƒê·∫£m b·∫£o kh√¥ng th√™m n·∫øu d·ªØ li·ªáu b·ªã l·ªói ho·∫∑c r·ªóng
                if (!data || Object.keys(data).length === 0) return prevChartData; 
                
                // Gi·ªØ gi·ªõi h·∫°n 30 ƒëi·ªÉm cho bi·ªÉu ƒë·ªì
                const updatedData = [...prevChartData, data].slice(-30); 
                return updatedData;
            });
        });

        // 3. Th·ª±c hi·ªán t·∫£i d·ªØ li·ªáu ban ƒë·∫ßu
        fetchInitialData();

        // 4. D·ªçn d·∫πp: Ng·∫Øt k·∫øt n·ªëi khi component unmount
        return () => {
            socket.off('connect');
            socket.off('sensorDataUpdate');
        };
    }, []); 

    if (loading) return <div className="loading-center">ƒêang t·∫£i Dashboard...</div>;
    
    
    const temp = latestData.temperature || '--';
    const light = latestData.light || '--';
    const humidity = latestData.humidity || '--';

    return (
        <div className="dashboard-container">
            {/* H√†ng 1: Th·∫ª C·∫£m bi·∫øn */}
            <div className="sensor-summary-cards">
                <SensorCard title="Nhi·ªát ƒë·ªô" value={temp} unit="¬∞C" icon="üå°Ô∏è" />
                <SensorCard title="√Ånh s√°ng" value={light} unit="Lux" icon="üí°" />
                <SensorCard title="ƒê·ªô ·∫©m" value={humidity} unit="%" icon="üíß" />
            </div>

            {/* H√†ng 2: ƒêi·ªÅu khi·ªÉn v√† Bi·ªÉu ƒë·ªì */}
            <div className="control-chart-area">
                
                {/* C·ªôt 1: ƒêi·ªÅu khi·ªÉn */}
                <div className="control-panel card">
                    <p className="panel-title">ƒêi·ªÅu khi·ªÉn</p> {/* ƒê·ªïi t√™n ti√™u ƒë·ªÅ */}
                    <div className="control-list">
                        {CONTROLLABLE_DEVICES.map(item => (
                            <DeviceCard 
                                key={item.device} 
                                device={item.device} 
                                label={item.label} 
                                mockupIcon={item.icon} 
                            />
                        ))}
                    </div>
                </div>

                {/* C·ªôt 2: Bi·ªÉu ƒë·ªì */}
                <div className="chart-panel card">
                    <SensorChart data={chartData} />
                </div>
            </div>
        </div>
    );
};

export default Dashboard;
