// src/pages/Dashboard.jsx (Phiên bản đã cập nhật với Socket.IO và Imports đầy đủ)

import React, { useEffect, useState } from 'react';
import { io } from 'socket.io-client';
import { getSensorData } from '../api';

// === IMPORT CÁC COMPONENTS CÒN THIẾU ===
import DeviceCard from '../components/DeviceCard'; 
import SensorChart from '../components/SensorChart';
import SensorCard from '../components/SensorCard';

// Khởi tạo kết nối Socket.IO Client
const socket = io('http://localhost:5000'); 

// Danh sách thiết bị điều khiển (Đã được code trước đó)
const CONTROLLABLE_DEVICES = [
    { device: 'light', label: 'Ánh sáng', icon: '💡' },
    { device: 'temp', label: 'Nhiệt độ', icon: '🌡️' },
    { device: 'humid', label: 'Độ ẩm', icon: '💧' },
];


const Dashboard = () => {
    // Sử dụng state để lưu trữ dữ liệu thời gian thực và dữ liệu biểu đồ
    const [latestData, setLatestData] = useState({});
    const [chartData, setChartData] = useState([]);
    const [loading, setLoading] = useState(true);

    // Dùng useEffect để quản lý kết nối Socket.IO và tải dữ liệu lịch sử
    useEffect(() => {
        
        // 1. Tải dữ liệu lịch sử (API REST) cho biểu đồ một lần khi component mount
        const fetchInitialData = async () => {
            try {
                const data = await getSensorData(30); 
                setChartData(data.reverse()); 
                if (data.length > 0) {
                    setLatestData(data[data.length - 1]); 
                }
            } catch (error) {
                console.error("Lỗi tải dữ liệu Dashboard ban đầu:", error);
                // Nếu lỗi 500/kết nối, vẫn cho phép Dashboard load với dữ liệu rỗng
            } finally {
                setLoading(false);
            }
        };

        // 2. Thiết lập lắng nghe Socket.IO (Real-time)
        socket.on('connect', () => {
            console.log('Socket.IO connected to server.');
        });
        
        // Lắng nghe sự kiện 'sensorDataUpdate' từ server.js
        socket.on('sensorDataUpdate', (data) => {
            console.log('Real-time data received:', data);
            
            // Cập nhật dữ liệu mới nhất
            setLatestData(data);
            
            // Cập nhật biểu đồ: Xóa bản ghi cũ nhất và thêm bản ghi mới
            setChartData(prevChartData => {
                // Đảm bảo không thêm nếu dữ liệu bị lỗi hoặc rỗng
                if (!data || Object.keys(data).length === 0) return prevChartData; 
                
                // Giữ giới hạn 30 điểm cho biểu đồ
                const updatedData = [...prevChartData, data].slice(-30); 
                return updatedData;
            });
        });

        // 3. Thực hiện tải dữ liệu ban đầu
        fetchInitialData();

        // 4. Dọn dẹp: Ngắt kết nối khi component unmount
        return () => {
            socket.off('connect');
            socket.off('sensorDataUpdate');
        };
    }, []); 

    if (loading) return <div className="loading-center">Đang tải Dashboard...</div>;
    
    
    const temp = latestData.temperature || '--';
    const light = latestData.light || '--';
    const humidity = latestData.humidity || '--';

    return (
        <div className="dashboard-container">
            {/* Hàng 1: Thẻ Cảm biến */}
            <div className="sensor-summary-cards">
                <SensorCard title="Nhiệt độ" value={temp} unit="°C" icon="🌡️" />
                <SensorCard title="Ánh sáng" value={light} unit="Lux" icon="💡" />
                <SensorCard title="Độ ẩm" value={humidity} unit="%" icon="💧" />
            </div>

            {/* Hàng 2: Điều khiển và Biểu đồ */}
            <div className="control-chart-area">
                
                {/* Cột 1: Điều khiển */}
                <div className="control-panel card">
                    <p className="panel-title">Điều khiển</p> {/* Đổi tên tiêu đề */}
                    <div className="control-list">
                        {CONTROLLABLE_DEVICES.map(item => (
                            <DeviceCard 
                                key={item.device} 
                                device={item.device} 
                                label={item.label} 
                                mockupIcon={item.icon} 
                            />
                        ))}
                    </div>
                </div>

                {/* Cột 2: Biểu đồ */}
                <div className="chart-panel card">
                    <SensorChart data={chartData} />
                </div>
            </div>
        </div>
    );
};

export default Dashboard;
