import React, { useEffect, useState, useMemo } from 'react';
import { getSensorData } from '../api'; // H√†m API l·∫•y d·ªØ li·ªáu
import Table from '../components/Table';
import './ActionsHistory.css';

const DataSensor = () => {
    const [rawSensorData, setRawSensorData] = useState([]);
    const [loading, setLoading] = useState(true);
    const [searchTerm, setSearchTerm] = useState('');
    const [searchType, setSearchType] = useState('all');
    const [sortBy, setSortBy] = useState('created_at');
    const [sortOrder, setSortOrder] = useState('desc');

    // Danh s√°ch ti√™u ƒë·ªÅ c·ªôt
    const sensorHeaders = ['id', 'temperature', 'humidity', 'light', 'created_at'];

    // T√πy ch·ªçn t√¨m ki·∫øm
    const searchOptions = [
        { value: 'all', label: 'T·∫•t c·∫£' },
        { value: 'created_at', label: 'Ng√†y/Th·ªùi gian' },
        { value: 'temperature', label: 'Nhi·ªát ƒë·ªô' },
        { value: 'humidity', label: 'ƒê·ªô ·∫©m' },
        { value: 'light', label: '√Ånh s√°ng' },
        { value: 'id', label: 'ID' },
    ];

    // üïí H√†m ƒë·ªãnh d·∫°ng th·ªùi gian chu·∫©n "HH:MM:SS DD/MM/YYYY"
    const formatTime = (timeString) => {
    if (!timeString) return '--';
    const date = new Date(timeString);
    const dd = String(date.getDate()).padStart(2, '0');
    const mm = String(date.getMonth() + 1).padStart(2, '0');
    const yyyy = date.getFullYear();
    const hh = String(date.getHours()).padStart(2, '0');
    const min = String(date.getMinutes()).padStart(2, '0');
    const ss = String(date.getSeconds()).padStart(2, '0');
    return `${hh}:${min}:${ss} ${dd}/${mm}/${yyyy}`; // ‚úÖ Kh√¥ng c√≤n d·∫•u ph·∫©y
};

    // üîÑ L·∫•y d·ªØ li·ªáu c·∫£m bi·∫øn
    useEffect(() => {
        const fetchData = async () => {
            try {
                const data = await getSensorData(1000);
                setRawSensorData(data);
            } catch (error) {
                console.error("L·ªói t·∫£i d·ªØ li·ªáu c·∫£m bi·∫øn:", error);
                setRawSensorData([]);
            } finally {
                setLoading(false);
            }
        };
        fetchData();
    }, []);

    // ‚öôÔ∏è X·ª≠ l√Ω l·ªçc + s·∫Øp x·∫øp d·ªØ li·ªáu
    const displayData = useMemo(() => {
        let currentData = Array.isArray(rawSensorData) ? [...rawSensorData] : [];

        // 1Ô∏è‚É£ L·ªçc (Filter)
        if (searchTerm) {
            const lowerSearch = searchTerm.toLowerCase();

            currentData = currentData.filter(item => {
                const formattedTime = formatTime(item.created_at).toLowerCase();

                // üß© H√†m chu·∫©n h√≥a th·ªùi gian ƒë·ªÉ h·ªó tr·ª£ nhi·ªÅu ki·ªÉu nh·∫≠p
                const normalizeTime = (timeStr) => {
                    // Lo·∫°i b·ªè d·∫•u ph·∫©y v√† chu·∫©n h√≥a ƒë·ªãnh d·∫°ng
                    const cleanStr = timeStr.replace(',', '').trim(); // "15:25:30 14/10/2025"
                    const [timePart = '', datePart = ''] = cleanStr.split(' ');

                    const [hh = '', mm = ''] = timePart.split(':');
                    const [day = '', month = '', year = ''] = datePart.split('/');

                    return {
                        full: cleanStr, // "15:25:30 14/10/2025"
                        timeOnly: `${hh}:${mm}`,
                        dateOnly: `${day}/${month}/${year}`,
                        dayMonth: `${day}/${month}`,
                        monthYear: `${month}/${year}`,
                        yearOnly: year
                    };
                };

                const t = normalizeTime(formattedTime);

                if (searchType === 'all') {
                    const searchFields = sensorHeaders.map(key => String(item[key] ?? '').toLowerCase());
                    const fullSearchString = searchFields.join(' ') + ' ' + formattedTime;
                    return fullSearchString.includes(lowerSearch);
                }

                // üîç T√¨m ki·∫øm theo th·ªùi gian
                else if (searchType === 'created_at') {
                    return (
                        t.full.includes(lowerSearch) ||
                        t.timeOnly.includes(lowerSearch) ||
                        t.dateOnly.includes(lowerSearch) ||
                        t.dayMonth.includes(lowerSearch) ||
                        t.monthYear.includes(lowerSearch) ||
                        t.yearOnly.includes(lowerSearch)
                    );
                }

                // üîç T√¨m ki·∫øm theo c·ªôt kh√°c
                else {
                    const value = item[searchType];
                    return String(value ?? '').toLowerCase().includes(lowerSearch);
                }
            });
        }

        // 2Ô∏è‚É£ S·∫Øp x·∫øp (Sort)
        if (sortBy) {
            currentData.sort((a, b) => {
                const aValue = a[sortBy];
                const bValue = b[sortBy];

                const numericColumns = ['id', 'temperature', 'humidity', 'light'];

                // üî¢ S·∫Øp x·∫øp s·ªë
                if (numericColumns.includes(sortBy)) {
                    const numA = parseFloat(aValue);
                    const numB = parseFloat(bValue);
                    if (numA < numB) return sortOrder === 'asc' ? -1 : 1;
                    if (numA > numB) return sortOrder === 'asc' ? 1 : -1;
                    return 0;
                }

                // üïí S·∫Øp x·∫øp th·ªùi gian
                if (sortBy === 'created_at') {
                    const aTime = new Date(aValue).getTime();
                    const bTime = new Date(bValue).getTime();
                    if (aTime < bTime) return sortOrder === 'asc' ? -1 : 1;
                    if (aTime > bTime) return sortOrder === 'asc' ? 1 : -1;
                    return 0;
                }

                // üî§ S·∫Øp x·∫øp chu·ªói
                const valA = String(aValue ?? '').toLowerCase();
                const valB = String(bValue ?? '').toLowerCase();
                if (valA < valB) return sortOrder === 'asc' ? -1 : 1;
                if (valA > valB) return sortOrder === 'asc' ? 1 : -1;
                return 0;
            });
        }

        // 3Ô∏è‚É£ Chu·∫©n b·ªã d·ªØ li·ªáu hi·ªÉn th·ªã
        const formattedData = currentData.map(item => ({
            ...item,
            created_at: formatTime(item.created_at)
        }));

        return formattedData;
    }, [rawSensorData, searchTerm, searchType, sortBy, sortOrder]);

    // üîÅ Thay ƒë·ªïi c·ªôt s·∫Øp x·∫øp
    const handleSortChange = (newSortBy) => {
        if (sortBy === newSortBy) {
            setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc');
        } else {
            setSortBy(newSortBy);
            setSortOrder('desc');
        }
    };

    if (loading) return <div className="loading-center">ƒêang t·∫£i d·ªØ li·ªáu c·∫£m bi·∫øn...</div>;

    const currentSearchLabel = searchOptions.find(o => o.value === searchType)?.label || 'T·∫•t c·∫£';

    return (
        <div className="actions-history-page">
            <h2>B·∫£ng D·ªØ li·ªáu C·∫£m bi·∫øn</h2>

            <div className="table-controls">
                <div className="search-group">
                    {/* Dropdown ch·ªçn lo·∫°i t√¨m ki·∫øm */}
                    <select
                        value={searchType}
                        onChange={(e) => setSearchType(e.target.value)}
                        className="filter-select"
                    >
                        {searchOptions.map(option => (
                            <option key={option.value} value={option.value}>
                                {option.label}
                            </option>
                        ))}
                    </select>

                    {/* √î t√¨m ki·∫øm */}
                    <input
                        type="text"
                        placeholder={`T√¨m ki·∫øm trong ${currentSearchLabel}...`}
                        value={searchTerm}
                        onChange={(e) => setSearchTerm(e.target.value)}
                    />

                    <button className="search-button">Search</button>
                </div>
            </div>

            <div className="table-container">
                <Table
                    headers={sensorHeaders}
                    data={displayData}
                    title="B·∫£ng D·ªØ li·ªáu C·∫£m bi·∫øn"
                    onSortChange={handleSortChange}
                    sortBy={sortBy}
                    sortOrder={sortOrder}
                />
            </div>
        </div>
    );
};

export default DataSensor;
