import React from "react";
import Table from "../components/Table";

export default function DataSensor({ data }) {
    const columns = [
        { header: "Time", accessor: (row) => new Date(row.timestamp).toLocaleString() },
        { header: "Temperature (°C)", accessor: "temperature" },
        { header: "Humidity (%)", accessor: "humidity" },
        { header: "Light", accessor: "light" },
    ];

    return (
        <div className="panel p-6">
            <h2 className="text-xl font-semibold mb-4">Sensor Data</h2>
            <Table
                columns={columns}
                data={data}
                enableDateFilter   // bật lọc theo thời gian
                rowsPerPageOptions={[5, 10, 25, 50]}
            />
        </div>
    );
}
