// src/api.js
import axios from 'axios';

// Đổi URL này nếu server của bạn chạy trên cổng khác
const API_BASE_URL = 'http://localhost:5000/api'; 

// === DEVICES/ACTIONS API ===

// POST /api/devices/:device (Điều khiển thiết bị)
export const controlDevice = async (device, status) => {
    // device: 'light', 'fan', 'ac'
    // status: 'ON' hoặc 'OFF'
    const response = await axios.post(`${API_BASE_URL}/devices/${device}`, {
        status: status
    });
    return response.data;
};

// GET /api/devices/actions (Lịch sử hành động)
export const getActionsHistory = async () => {
    const response = await axios.get(`${API_BASE_URL}/devices/actions`);
    return response.data;
};

// === SENSORS API ===

// GET /api/sensors (Lấy dữ liệu cảm biến lịch sử)
export const getSensorData = async (limit = 50) => {
    const response = await axios.get(`${API_BASE_URL}/sensors?limit=${limit}`);
    return response.data;
};
