import React from "react";
import Table from "../components/Table";

export default function ActionsHistory({ actions }) {
    const columns = [
        { header: "Time", accessor: (row) => new Date(row.timestamp).toLocaleString() },
        { header: "Device", accessor: "device" },
        { header: "Action", accessor: "action" },
        { header: "User", accessor: "user" },
    ];

    return (
        <div className="panel p-6">
            <h2 className="text-xl font-semibold mb-4">Actions History</h2>
            <Table
                columns={columns}
                data={actions}
                enableDeviceFilter
                enableDateFilter
            />
        </div>
    );
}
