// src/pages/Dashboard.jsx

import React, { useEffect, useState } from 'react';
import { io } from 'socket.io-client';
// Import Named Export 'getSensorData'
import { getSensorData } from '../api'; 

// === IMPORT C√ÅC COMPONENTS ===
// S·ª≠a l·ªói: Import component ControlCard v√† ƒë·ªïi t√™n n√≥ th√†nh DeviceCard ngay t·∫°i ƒë√¢y
import DeviceCard from '../components/DeviceCard'; 
import SensorChart from '../components/SensorChart';
import SensorCard from '../components/SensorCard';

// === IMPORT CSS V√Ä KH·ªûI T·∫†O H·∫∞NG S·ªê ===
//import './Dashboard.css'; // ƒê·∫£m b·∫£o b·∫°n ƒë√£ t·∫°o file n√†y
const socket = io('http://localhost:5000'); // Kh·ªüi t·∫°o Socket.IO Client

const CONTROLLABLE_DEVICES = [
    { device: 'light', label: 'ƒê√®n Chi·∫øu S√°ng', icon: 'üí°' },
    { device: 'fan', label: 'Qu·∫°t Th√¥ng Gi√≥', icon: 'üí®' }, // Gi·∫£ ƒë·ªãnh fan thay v√¨ temp/humid
    { device: 'ac', label: 'M√°y L·∫°nh', icon: '‚ùÑÔ∏è' }, // Gi·∫£ ƒë·ªãnh ac
];

const Dashboard = () => {
    const [latestData, setLatestData] = useState({});
    const [chartData, setChartData] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        
        // 1. T·∫£i d·ªØ li·ªáu l·ªãch s·ª≠ (API REST)
        const fetchInitialData = async () => {
            try {
                // T·∫£i 30 b·∫£n ghi g·∫ßn nh·∫•t
                const data = await getSensorData(30); 
                // Bi·ªÉu ƒë·ªì c·∫ßn d·ªØ li·ªáu theo th·ª© t·ª± th·ªùi gian tƒÉng d·∫ßn
                const sortedData = data.reverse(); 
                
                setChartData(sortedData); 
                if (sortedData.length > 0) {
                    // D·ªØ li·ªáu m·ªõi nh·∫•t l√† b·∫£n ghi cu·ªëi c√πng sau khi ƒë√£ ƒë·∫£o ng∆∞·ª£c
                    setLatestData(sortedData[sortedData.length - 1]); 
                }
            } catch (error) {
                console.error("L·ªói t·∫£i d·ªØ li·ªáu Dashboard ban ƒë·∫ßu:", error.message);
                // X·ª≠ l√Ω khi Backend g·∫∑p l·ªói (v√≠ d·ª•: l·ªói 500 DB)
            } finally {
                setLoading(false);
            }
        };

        // 2. Thi·∫øt l·∫≠p l·∫Øng nghe Socket.IO (Real-time)
        socket.on('connect', () => {
            console.log('Socket.IO connected to server.');
        });
        
        // L·∫Øng nghe s·ª± ki·ªán 'sensorDataUpdate' t·ª´ server.js
        socket.on('sensorDataUpdate', (data) => {
            console.log('Real-time data received:', data);
            
            // C·∫≠p nh·∫≠t d·ªØ li·ªáu m·ªõi nh·∫•t
            setLatestData(data);
            
            // C·∫≠p nh·∫≠t bi·ªÉu ƒë·ªì (gi·ªØ gi·ªõi h·∫°n 30 ƒëi·ªÉm)
            setChartData(prevChartData => {
                const updatedData = [...prevChartData, data].slice(-30); 
                return updatedData;
            });
        });

        fetchInitialData();

        // 3. D·ªçn d·∫πp: Ng·∫Øt l·∫Øng nghe khi component unmount
        return () => {
            socket.off('connect');
            socket.off('sensorDataUpdate');
        };
    }, []); 

    if (loading) return <div className="loading-center">ƒêang t·∫£i Dashboard...</div>;
    
    // ƒê·∫£m b·∫£o gi√° tr·ªã hi·ªÉn th·ªã l√† s·ªë ho·∫∑c '--'
    const temp = latestData.temperature || '--';
    const light = latestData.light || '--';
    const humidity = latestData.humidity || '--';

    return (
        <div className="dashboard-container">
            {/* H√†ng 1: Th·∫ª C·∫£m bi·∫øn */}
            <div className="sensor-summary-cards">
                <SensorCard title="Nhi·ªát ƒë·ªô" value={temp} unit="¬∞C" icon="üå°Ô∏è" />
                <SensorCard title="ƒê·ªô ·∫©m" value={humidity} unit="%" icon="üíß" />
                <SensorCard title="√Ånh s√°ng" value={light} unit="Lux" icon="üí°" />
            </div>

            {/* H√†ng 2: ƒêi·ªÅu khi·ªÉn v√† Bi·ªÉu ƒë·ªì */}
            <div className="control-chart-area">
                
                {/* C·ªôt 1: ƒêi·ªÅu khi·ªÉn */}
                <div className="control-panel card">
                    <p className="panel-title">ƒêi·ªÅu khi·ªÉn Thi·∫øt b·ªã</p>
                    <div className="control-list">
                        {CONTROLLABLE_DEVICES.map(item => (
                            <DeviceCard 
                                key={item.device} 
                                device={item.device} 
                                label={item.label} 
                                mockupIcon={item.icon} 
                            />
                        ))}
                    </div>
                </div>

                {/* C·ªôt 2: Bi·ªÉu ƒë·ªì */}
                <div className="chart-panel card">
                    <SensorChart data={chartData} />
                </div>
            </div>
        </div>
    );
};

export default Dashboard;
