// src/pages/Profile.jsx (Final Version - Ho√†n ch·ªânh)

import React, { useState, useRef } from 'react';
import './Profile.css';
// import { updateProfileAPI } from '../api'; // Gi·∫£ ƒë·ªãnh h√†m API

const Profile = () => {
    const fileInputRef = useRef(null);
    // State l∆∞u tr·ªØ File object ƒë·ªÉ g·ª≠i l√™n server
    const [avatarFile, setAvatarFile] = useState(null);

    const [userData, setUserData] = useState({
        fullName: 'L√™ ƒê·ª©c To√†n',
        studentId: 'B22DCCN730',
        github: 'https://github.com/ToanVY/IoT.git',
        figma: 'https://www.figma.com/design/R03h45Jnuu58OsxUIhrxlv/IoT?node-id=0-1',
        email: 'letoan5204@gmail.com',
        api: 'http://localhost:5000/apis/', 
        avatarUrl: '/assets/profile-avatar.png'
    });

    const [isEditing, setIsEditing] = useState(false);

    // X·ª≠ l√Ω thay ƒë·ªïi Input text
    const handleChange = (e) => {
        const { name, value } = e.target;
        setUserData(prevData => ({
            ...prevData,
            [name]: value
        }));
    };
    
    // X·ª≠ l√Ω ch·ªçn ·∫£nh m·ªõi v√† t·∫°o preview
    const handleAvatarChange = (e) => {
        const file = e.target.files[0];
        if (file && file.type.startsWith('image/')) {
            setAvatarFile(file); 
            
            const reader = new FileReader();
            reader.onloadend = () => {
                setUserData(prevData => ({
                    ...prevData,
                    avatarUrl: reader.result // C·∫≠p nh·∫≠t URL ƒë·ªÉ xem tr∆∞·ªõc
                }));
            };
            reader.readAsDataURL(file);
        }
    };
    
    // K√≠ch ho·∫°t input file (ch·ªâ khi ƒëang ·ªü ch·∫ø ƒë·ªô S·ª≠a)
    const triggerFileInput = () => {
        if (isEditing) {
             fileInputRef.current.click();
        }
    };

    // LOGIC G·ª¨I D·ªÆ LI·ªÜU L√äN SERVER
    const handleEditToggle = async () => {
        if (isEditing) {
            try {
                const formData = new FormData();
                
                // Th√™m file ·∫£nh (n·∫øu c√≥)
                if (avatarFile) {
                    formData.append('avatar', avatarFile);
                }

                // Th√™m d·ªØ li·ªáu text
                formData.append('fullName', userData.fullName);
                formData.append('studentId', userData.studentId);
                formData.append('github', userData.github);
                formData.append('figma', userData.figma);
                formData.append('email', userData.email);
                formData.append('api', userData.api);
                
                console.log('ƒêang g·ª≠i d·ªØ li·ªáu l√™n server...');
                
                // G·ªåI API L∆ØU D·ªÆ LI·ªÜU ·ªû ƒê√ÇY
                // const result = await updateProfileAPI(formData); 
                
                setAvatarFile(null); 
                alert('C·∫≠p nh·∫≠t h·ªì s∆° th√†nh c√¥ng!');

            } catch (error) {
                console.error("L·ªói khi l∆∞u d·ªØ li·ªáu:", error);
                alert('L·ªói khi c·∫≠p nh·∫≠t h·ªì s∆°. Vui l√≤ng th·ª≠ l·∫°i.');
                return; 
            }
        }
        
        // Chuy·ªÉn ƒë·ªïi tr·∫°ng th√°i ch·ªânh s·ª≠a
        setIsEditing(!isEditing);
    };


    // H√†m helper render input ho·∫∑c gi√° tr·ªã tƒ©nh/link
    const renderField = (name, type = 'text') => {
        const value = userData[name];

        if (isEditing) {
            // Ch·∫ø ƒë·ªô ch·ªânh s·ª≠a: D√πng Input
            return (
                <input
                    type={type}
                    name={name}
                    value={value}
                    onChange={handleChange}
                    readOnly={false}
                    className="editable"
                />
            );
        }
        
        // Ch·∫ø ƒë·ªô Xem (Read-only)
        
        // X·ª≠ l√Ω ƒë·∫∑c bi·ªát cho c√°c tr∆∞·ªùng l√† URL (Link)
        if (name === 'github' || name === 'figma' || name === 'api') {
            return (
                <a 
                    href={value} 
                    target="_blank" 
                    rel="noopener noreferrer" 
                    className="profile-link"
                >
                    {value}
                </a>
            );
        }
        
        // Tr∆∞·ªùng th√¥ng th∆∞·ªùng: D√πng <div> tƒ©nh
        return (
            <div className="profile-value">
                {value}
            </div>
        );
    };


    return (
        <div className="profile-page">
            <div className="profile-card">
                
                {/* -------------------- HEADER -------------------- */}
                <div className="profile-header">
                    
                    <div className="avatar-section">
                        {/* K√≠ch ho·∫°t input file khi click v√†o placeholder (ch·ªâ khi ƒëang S·ª≠a) */}
                        <div className="avatar-placeholder" onClick={triggerFileInput}>
                            <img src={userData.avatarUrl} alt="·∫¢nh ƒë·∫°i di·ªán" className="profile-avatar"/>
                            
                            {/* INPUT FILE ·∫®N */}
                            <input
                                type="file"
                                ref={fileInputRef}
                                onChange={handleAvatarChange}
                                accept="image/*"
                                style={{ display: 'none' }}
                                disabled={!isEditing}
                            />
                        </div>
                        
                        {/* N√∫t Thay ƒë·ªïi ch·ªâ hi·ªán khi ƒëang ch·ªânh s·ª≠a */}
                        {isEditing && (
                           <button className="btn-secondary" onClick={triggerFileInput}>Thay ƒë·ªïi</button>
                        )}
                    </div>

                    <div className="info-intro">
                        <h3>{userData.fullName}</h3>
                        <p>{userData.studentId}</p>
                    </div>

                    <button 
                        className="btn-primary" 
                        onClick={handleEditToggle}
                    >
                        {isEditing ? 'L∆∞u' : 'S·ª≠a'}
                    </button>
                </div>

                {/* -------------------- DETAILS -------------------- */}
                <div className="profile-details">
                    
                    {/* H·ªå V√Ä T√äN */}
                    <div className="detail-row">
                        <label>H·ªç v√† t√™n</label>
                        {renderField('fullName')}
                    </div>

                    {/* M√É SV */}
                    <div className="detail-row">
                        <label>M√£ SV</label>
                        {renderField('studentId')}
                    </div>

                    {/* GITHUB */}
                    <div className="detail-row">
                        <label>Github</label>
                        {renderField('github')}
                    </div>
                    
                    {/* FIGMA */}
                    <div className="detail-row">
                        <label>Figma</label>
                        {renderField('figma')}
                    </div>
                    
                    {/* API */}
                    <div className="detail-row">
                        <label>API</label>
                        {renderField('api')}
                    </div>

                    {/* EMAIL */}
                    <div className="detail-row">
                        <label>Email</label>
                        {renderField('email', 'email')}
                        {/* Icon Email ch·ªâ hi·ªán khi kh√¥ng ch·ªânh s·ª≠a */}
                        {!isEditing && (
                            <span className="email-icon">üìß</span>
                        )}
                    </div>

                </div>
            </div>
        </div>
    );
};

export default Profile;
