// src/pages/ActionsHistory.jsx

import React, { useEffect, useState, useMemo } from 'react';
import { getActionsHistory } from '../api';
import Table from '../components/Table';
// import './ActionsHistory.css'; // Dùng CSS nếu cần

const ActionsHistory = () => {
    const [actionsData, setActionsData] = useState([]);
    const [loading, setLoading] = useState(true);
    const [searchTerm, setSearchTerm] = useState('');
    const [sortBy, setSortBy] = useState('Time'); // Sắp xếp mặc định theo cột 'Time'
    const [sortOrder, setSortOrder] = useState('desc'); 
    
    // Định nghĩa headers (Khớp với tên cột trong DB Actions)
    const actionHeaders = ['id', 'Device', 'Status', 'Time'];

    useEffect(() => {
        const fetchData = async () => {
            try {
                const data = await getActionsHistory(); 
                setActionsData(data);
            } catch (error) {
                console.error("Lỗi tải lịch sử hành động:", error);
                setActionsData([]);
            } finally {
                setLoading(false);
            }
        };
        fetchData();
    }, []);

    // === LOGIC TÌM KIẾM VÀ SẮP XẾP ===
    const filteredAndSortedData = useMemo(() => {
        let currentData = Array.isArray(actionsData) ? actionsData : [];

        // 1. Lọc (Filter)
        if (searchTerm) {
            const lowerCaseSearch = searchTerm.toLowerCase();
            currentData = currentData.filter(item => 
                Object.values(item).some(value => 
                    String(value).toLowerCase().includes(lowerCaseSearch)
                )
            );
        }

        // 2. Sắp xếp (Sort)
        if (sortBy) {
            currentData.sort((a, b) => {
                const aValue = a[sortBy];
                const bValue = b[sortBy];
                
                if (aValue < bValue) return sortOrder === 'asc' ? -1 : 1;
                if (aValue > bValue) return sortOrder === 'asc' ? 1 : -1;
                return 0;
            });
        }

        return currentData;
    }, [actionsData, searchTerm, sortBy, sortOrder]);
    // ======================================

    const handleSortChange = (newSortBy) => {
        if (sortBy === newSortBy) {
            setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc');
        } else {
            setSortBy(newSortBy);
            setSortOrder('desc'); 
        }
    };
    
    if (loading) return <div className="loading-center">Đang tải lịch sử hành động...</div>;

    return (
        <div className="page-container">
            {/* Header và Controls (Theo thiết kế) */}
            <div className="table-controls-bar">
                <input
                    type="text"
                    placeholder="Tìm kiếm..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    className="search-input"
                />
                <button className="search-button">Search</button>
            </div>

            {/* Bảng Dữ liệu */}
            <Table 
                headers={actionHeaders} 
                data={filteredAndSortedData} 
                title="Lịch sử Hành động Điều khiển"
                onSortChange={handleSortChange} 
                sortBy={sortBy}
                sortOrder={sortOrder}
            />
        </div>
    );
};

export default ActionsHistory;
