// src/components/DeviceCard.jsx
import React from 'react';
import './DeviceCard.css';

// Thêm prop isOnline
const DeviceCard = ({ device, label, status, onToggle, loading, isOnline }) => { 
    const newStatus = status === 'on' ? 'off' : 'on';
    const isDeviceOn = status === 'on';
    
    let icon = '⚙️';
    let themeClass = 'default';

    switch (device) {
        case 'light':
            icon = '💡';
            themeClass = isDeviceOn && isOnline ? 'on-light' : 'off-light';
            break;
        case 'fan':
            icon = '🌀';
            themeClass = isDeviceOn && isOnline ? 'on-fan' : 'off-fan';
            break;
        case 'ac':
            icon = 'ac-unit';
            themeClass = isDeviceOn && isOnline ? 'on-ac' : 'off-ac';
            break;
        default:
            break;
    }

    // Điều chỉnh theme khi thiết bị OFFLINE
    const cardClass = isOnline ? themeClass : 'off-general'; 

    const handleToggle = () => {
        // Thêm kiểm tra isOnline
        if (!loading && isOnline) { 
            onToggle(device, newStatus);
        }
    };

    return (
        <div className={`device-card card ${cardClass}`}>
            <div className={`device-icon ${icon === 'ac-unit' ? 'ac-unit' : ''}`}>
                {icon === 'ac-unit' ? '' : icon}
            </div>
            <h4 className="device-label">{label}</h4>
            <p className={`device-status status-${status}`}>
                Trạng thái: <strong>{isOnline ? (isDeviceOn ? 'ĐANG BẬT' : 'ĐANG TẮT') : 'NGOẠI TUYẾN'}</strong>
            </p>
            
            {/* Nút Bật/Tắt hoặc Loading */}
            {loading || !isOnline ? ( // Luôn disabled/loading khi offline
                <button className={`toggle-button ${!isOnline ? 'btn-disabled' : 'btn-loading'}`} disabled>
                    {!isOnline ? 'OFFLINE' : <span className="spinner"></span>}
                </button>
            ) : (
                <button 
                    className={`toggle-button toggle-${newStatus}`}
                    onClick={handleToggle}
                >
                    {isDeviceOn ? 'TẮT' : 'BẬT'}
                </button>
            )}
        </div>
    );
};

export default DeviceCard;
