// frontend/src/pages/ActionsHistory.jsx

import React, { useEffect, useState } from 'react';
import { getActionsHistory } from '../api'; // Import hàm API
import './ActionsHistory.css'; // File CSS cho trang này

const ActionsHistory = () => {
    const [actions, setActions] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchActions = async () => {
            try {
                const data = await getActionsHistory();
                setActions(data);
            } catch (err) {
                console.error("Lỗi khi tải lịch sử hành động:", err);
                setError("Không thể tải dữ liệu lịch sử. Kiểm tra Server Backend và CSDL.");
            } finally {
                setIsLoading(false);
            }
        };

        fetchActions();
    }, []);

    // Hàm định dạng thời gian
    const formatTime = (timeString) => {
        if (!timeString) return '--';
        const date = new Date(timeString);
        // Định dạng Việt Nam: DD/MM/YYYY HH:MM:SS
        return date.toLocaleString('vi-VN', {
            year: 'numeric',
            month: '2-digit',
            day: '2-digit',
            hour: '2-digit',
            minute: '2-digit',
            second: '2-digit'
        });
    };

    if (isLoading) {
        return <div className="actions-history-page">Đang tải lịch sử...</div>;
    }

    if (error) {
        return <div className="actions-history-page error">{error}</div>;
    }

    return (
        <div className="actions-history-page">
            <h2>Lịch sử Hành động Điều khiển</h2>

            <div className="table-container">
                <table className="actions-table">
                    <thead>
                        <tr>
                            <th>ID</th>
                            <th>Thiết bị (device)</th>
                            <th>Trạng thái (status)</th>
                            <th>Thời gian (created_at)</th>
                        </tr>
                    </thead>
                    <tbody>
                        {actions.length === 0 ? (
                            <tr>
                                <td colSpan="4">Chưa có hành động nào được ghi lại.</td>
                            </tr>
                        ) : (
                            actions.map((action) => (
                                <tr key={action.ID}> 
                                    {/* CHÚ Ý: Đảm bảo tên trường (ID, Device, Status, Time) khớp với cột trong CSDL MySQL */}
                                    <td>{action.ID}</td>
                                    <td>{action.Device}</td>
                                    <td className={`status-${action.Status.toLowerCase()}`}>{action.Status}</td>
                                    <td>{formatTime(action.Time)}</td>
                                </tr>
                            ))
                        )}
                    </tbody>
                </table>
            </div>
        </div>
    );
};

export default ActionsHistory;
