// frontend/src/pages/ActionsHistory.jsx
import React, { useEffect, useState, useMemo } from "react";
import { getActionsHistory } from "./src/api";
import Table from "../components/Table";
import "./ActionsHistory.css";

function ActionsHistory() {
  const [actions, setActions] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [searchType, setSearchType] = useState("ID"); // ch·ªâ ID, Action, Time
  const [deviceFilter, setDeviceFilter] = useState(""); // Light, Fan, AC
  const [statusFilter, setStatusFilter] = useState(""); // ON, OFF
  const [sortConfig, setSortConfig] = useState({ key: "ID", direction: "asc" });
  const [error, setError] = useState(null);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    const fetchActions = async () => {
      try {
        const data = await getActionsHistory();
        setActions(data);
      } catch (err) {
        setError("Kh√¥ng th·ªÉ t·∫£i d·ªØ li·ªáu l·ªãch s·ª≠ h√†nh ƒë·ªông.");
      } finally {
        setIsLoading(false);
      }
    };
    fetchActions();
  }, []);

  const filteredAndSortedActions = useMemo(() => {
    let currentActions = actions.slice();

    // üîç L·ªçc theo searchTerm (ID, Action, Time)
    if (searchTerm.trim() !== "") {
      currentActions = currentActions.filter((action) => {
        const value = action[searchType] || "";
        return value.toString().toLowerCase().includes(searchTerm.toLowerCase());
      });
    }

    // üìå L·ªçc theo thi·∫øt b·ªã
    if (deviceFilter !== "") {
      currentActions = currentActions.filter(
        (action) => action.Device.toLowerCase() === deviceFilter.toLowerCase()
      );
    }

    // üìå L·ªçc theo tr·∫°ng th√°i
    if (statusFilter !== "") {
      currentActions = currentActions.filter(
        (action) => action.Status.toLowerCase() === statusFilter.toLowerCase()
      );
    }

    // üîΩ S·∫Øp x·∫øp
    currentActions.sort((a, b) => {
      let aValue = a[sortConfig.key];
      let bValue = b[sortConfig.key];

      if (sortConfig.key === "ID" || sortConfig.key === "id") {
        aValue = parseInt(a.ID || a.id || 0, 10);
        bValue = parseInt(b.ID || b.id || 0, 10);
      } else {
        aValue = aValue?.toString().toLowerCase() || "";
        bValue = bValue?.toString().toLowerCase() || "";
      }

      if (aValue < bValue) return sortConfig.direction === "asc" ? -1 : 1;
      if (aValue > bValue) return sortConfig.direction === "asc" ? 1 : -1;
      return 0;
    });

    return currentActions;
  }, [actions, searchTerm, searchType, deviceFilter, statusFilter, sortConfig]);

  const formatTime = (timeString) => {
    const date = new Date(timeString);
    return date.toLocaleString("vi-VN", {
      dateStyle: "short",
      timeStyle: "short",
    });
  };

  const handleSortChange = (key) => {
    setSortConfig((prev) => ({
      key,
      direction: prev.key === key && prev.direction === "asc" ? "desc" : "asc",
    }));
  };

  if (isLoading) return <div className="loading">‚è≥ ƒêang t·∫£i l·ªãch s·ª≠...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <div className="actions-history-page">
      <h2>L·ªãch s·ª≠ h√†nh ƒë·ªông</h2>

      <div className="table-controls">
        {/* üîç Nh√≥m search */}
        <div className="search-group">
          <select
            className="filter-select"
            value={searchType}
            onChange={(e) => setSearchType(e.target.value)}
          >
            <option value="ID">ID</option>
            <option value="Action">Action</option>
            <option value="Time">Time</option>
          </select>
          <input
            type="text"
            placeholder={`T√¨m ki·∫øm theo ${searchType}...`}
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
        </div>

        {/* üìå B·ªô l·ªçc thi·∫øt b·ªã */}
        <div className="filter-buttons">
          <span>Thi·∫øt b·ªã:</span>
          <button
            className={deviceFilter === "Light" ? "active" : ""}
            onClick={() => setDeviceFilter(deviceFilter === "Light" ? "" : "Light")}
          >
            Light
          </button>
          <button
            className={deviceFilter === "Fan" ? "active" : ""}
            onClick={() => setDeviceFilter(deviceFilter === "Fan" ? "" : "Fan")}
          >
            Fan
          </button>
          <button
            className={deviceFilter === "AC" ? "active" : ""}
            onClick={() => setDeviceFilter(deviceFilter === "AC" ? "" : "AC")}
          >
            AC
          </button>
        </div>

        {/* üìå B·ªô l·ªçc tr·∫°ng th√°i */}
        <div className="filter-buttons">
          <span>Tr·∫°ng th√°i:</span>
          <button
            className={statusFilter === "ON" ? "active" : ""}
            onClick={() => setStatusFilter(statusFilter === "ON" ? "" : "ON")}
          >
            ON
          </button>
          <button
            className={statusFilter === "OFF" ? "active" : ""}
            onClick={() => setStatusFilter(statusFilter === "OFF" ? "" : "OFF")}
          >
            OFF
          </button>
        </div>
      </div>

      <div className="table-container">
        <Table
          headers={[
            { key: "ID", label: "ID", align: "right", sortable: true },
            { key: "Action", label: "H√†nh ƒë·ªông", sortable: true },
            { key: "Device", label: "Thi·∫øt b·ªã", sortable: true },
            {
              key: "Status",
              label: "Tr·∫°ng th√°i",
              render: (value) => (
                <span className={value === "ON" ? "status-on" : "status-off"}>
                  {value}
                </span>
              ),
              sortable: true,
            },
            { key: "Time", label: "Th·ªùi gian", render: formatTime, sortable: true },
          ]}
          data={filteredAndSortedActions}
          onSortChange={handleSortChange}
          sortConfig={sortConfig}
        />
      </div>
    </div>
  );
}

export default ActionsHistory;
